    width:50%;
    display:flex;
    flex-direction:column;
    justify-content:center;
`
export const ContainerRight = styled.div`
    width:50%;
    display:flex;
    justify-content:center;
`
export const Titulo = styled.p`
    font-size:3em;
    font-family: 'Sora', sans-serif;
`
export const Img = styled.img`
    width:500px;
    height:500px;
`
export const Paragrafo = styled.p`
`
